import React from "react";
import axios from "axios";
import "./styles.css";
import { useState } from "react";

export default function Contact() {
  const [post,setpost] = useState({
    title:"",
    email:"",
    msg:""
  })

  const handlechange = (event)=>{
    console.log(event);
    setpost({...post,[event.target.name] : event.target.value });
  }

  

  const handlesubmit = async (event) =>{
    console.log("click")
    event.preventDefault();
    // let userData={
    //   title:"",
    // email:"",
    // msg:""
    // }
    fetch("/api/formData",{
      method:'post',
      headers:{
        "Content-Type": "application/json"
      },
      body:JSON.stringify(post)
    }).then(response=>response.json()).then(data=>{
      console.log(data)
    })
    console.log("clicked");
  }

  return (
    <div className="contact" id="Contact" >
      <div className="contact-heading">
        <h2>Contact Me</h2>
        <p>Submit the form below to get in touch with me:- </p>
      </div>

      <form className="contact-form" onSubmit={handlesubmit}>
        <input type="text" name="title" value={post.title} onChange={handlechange}  placeholder="Enter your name"></input>
        <input type="email" name="email" value={post.email} onChange={handlechange}  placeholder="Enter your e-mail"></input>
        <textarea placeholder="Enter Text" value={post.msg} onChange={handlechange}  name="msg" id="" cols="30" rows="10"></textarea>
        <button type="submit">Let's Talk</button>
      </form>
    </div>
  );
}
